(()=>{"use strict";document.addEventListener("DOMContentLoaded",(()=>{document.getElementById("weather-form").addEventListener("submit",(async t=>{t.preventDefault();const e=document.getElementById("weather-location"),n=document.getElementById("weather-icon"),o=document.getElementById("weather-name"),r=e.value;console.log(r);try{const t=await async function(t){try{const e=await fetch(`https://api.weatherapi.com/v1/current.json?key=d497700796c940518d932911230608&q=${t}`,{mode:"cors"});if(!e.ok)throw new Error("Network response was not ok");return await e.json()}catch(t){throw new Error("Error fetching weather data:",t)}}(r);console.log(t);const e=t.current.condition.icon,c=t.current.condition.text;n.src=e,o.textContent=c}catch(t){console.error(t)}}))}))})();