(()=>{"use strict";document.addEventListener("DOMContentLoaded",(()=>{const e=document.getElementById("weather-form"),t=document.getElementById("weather-location"),n=document.getElementById("country"),r=document.getElementById("city"),o=document.getElementById("weather-name"),c=document.getElementById("weather-time"),i=document.getElementById("weather-icon"),d=document.getElementById("temperatureDegree"),a=document.getElementById("windDirection"),u=document.getElementById("windDegree");e.addEventListener("submit",(async e=>{e.preventDefault();const m=t.value;try{const e=await async function(e){try{const t=await fetch(`https://api.weatherapi.com/v1/current.json?key=d497700796c940518d932911230608&q=${e}`,{mode:"cors"});if(!t.ok)throw new Error("Network response was not ok");return await t.json()}catch(e){throw new Error("Error fetching weather data:",e)}}(m);console.log(e);const t={country:e.location.country,city:e.location.region,state:e.current.condition.text,lastUpdated:e.current.last_updated,iconUrl:e.current.condition.icon,humidity:e.current.humidity,temperatureC:e.current.temp_c,temperatureF:e.current.temp_f,windDirection:e.current.wind_dir,windKph:e.current.wind_kph,windMph:e.current.wind_mph};i.src=t.iconUrl,n.innerText=t.country,r.innerText=t.city,o.innerText=t.state,c.innerText=t.lastUpdated,d.innerText=t.temperatureC,a.innerText=t.windDirection,u.innerText=t.windKph}catch(e){console.error(e)}}))}))})();